mapper.select:
SELECT I.ISSUE_ID, I.PROJECT_ID, I.ISSUE_TYPE, I.ISSUE_STATUS, I.ISSUE_DESCRIPTION, I.ISSUE_DETAIL, I.ISSUE_CONTRACT_ID, I.ISSUE_REPORTER_ID, I.ISSUE_PRIORITY, I.CREATE_AT, I.TOP_ISSUE FROM ISSUE I, PROJECT P, PROJECT_MEMBER PM WHERE I.PROJECT_ID = P.PROJECT_ID AND P.PROJECT_ID = PM.PROJECT_ID AND PM.MEMBER_ID = #{member} AND (PM.ROLE IN ('CREATOR', 'MEMBER'))
AND P.project_id = #{project}
AND ${customSqlCondition}
ORDER BY #{order} #{sort};

SELECT PROJECT_ID
FROM ISSUE I
WHERE ISSUE_ID = #{issueId}

mapper.update:
UPDATE ISSUE
SET TOP_ISSUE = NULL
WHERE TOP_ISSUE = #{issueId}
