mapper.select:
SELECT ROLE FROM PROJECT_MEMBER
WHERE PROJECT_ID = #{projectId}
    AND MEMBER_ID = #{memberId}
    AND ROLE = #{role}

mapper.update:
DELETE PROJECT_MEMBER
WHERE PROJECT_ID = #{projectId}
    AND MEMBER_ID = #{memberId}

UPDATE PROJECT_MEMBER
SET ROLE = #{role}
WHERE PROJECT_ID = #{projectId}
    AND MEMBER_ID = #{memberId}
